import ApexCharts from "react-apexcharts";
import $ from 'jquery';
function Chart() {

    const data = JSON.parse(window.localStorage.getItem('total'));
    const deathCntData = [];
    const decideCntData = [];
    const createDtData = [];

    data.forEach(element => {
        deathCntData.push(element.deathCnt);
        decideCntData.push(element.decideCnt);
        createDtData.push(element.createDt);
    });

    const day = createDtData.map((e) => {
      const month = e.split("-")[1];
      const day = e.split('-')[2].split(" ")[0];
      return `${month}월 ${day}일`;
    });

console.log(deathCntData, decideCntData, day)

    const opstions = {
      chart: {
        type: 'bar'
      },
      series: [{
        name: "사망자",
        data: deathCntData.sort()
      },
    {
      name: "확진자",
      data: decideCntData.sort()
    },{
      name: 'Free Cash Flow',
      data: [35, 41, 36, 26, 45, 48, 52, 53, 41]
    }],
    xaxis: {
      categories: day.sort(),
    },
    }

    return <div>
       <ApexCharts
       options={opstions}
       series={opstions.series}
       type="bar"
       width="700px"
       height="300px"
     />
    </div> 
}
export default Chart;
