import $ from 'jquery';
import "../css/World.css";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft,faChevronRight } from "@fortawesome/free-solid-svg-icons"
function World() {

    const world_code = ['RU','CN','US',"IN","DE","FR","JP","GB","IL"];
    const DataList = [];
    const world = {
        init() {
            this.WorldDate();
            this.ClickArrow.bind(this);
        },
        WorldDate() {
            $.ajax({
                type : "GET",
                url:"https://api.covid19api.com/summary",
                dataType: 'json',
                error: function (err) { 
                    console.error(err);
                },
                success:function (res) {
                    const list = res.Countries;//
                    for (let i = 0; i < list.length; i++) {
                        const { CountryCode } = list[i];
                        world_code.forEach(code => {
                            if(code == CountryCode) {
                                DataList.push(list[i]);
                            }
                        })
                    }
                },
            });

            // console.log(DataList);
        },
        ClickArrow(e) {
            console.log(e.target);

        }
    };

    window.onload = () => {
        world.init();
    }

    return <div className="World_wrap">
        <div className="right arrow" onClick={(e) => world.ClickArrow(e)} >
            <p>
                <FontAwesomeIcon data_num="1" icon={faChevronRight}/>
            </p>        
        </div>
        <div className="left arrow"  onClick={(e) => world.ClickArrow(e)} >
            <p>
                <FontAwesomeIcon data_num="2" icon={faChevronLeft}/></p>
        </div>
        <div className="earth_img_box"></div>
    </div>
}

export default World;
