    //         $('.left').animate({ opacity: "1" });
    //         setTimeout(() => {
    //             this.ListDraw(num);
    //         },1500);
    //     }

    //     if(num == 2 ){
    //         $('.earth_img_box').css({ transform: "rotate(0deg)" });
    //         $('.right').animate({ opacity: "1" });
    //         $('.left').animate({ opacity: "0" });
    //         setTimeout(() => {
    //             this.ListDraw(num);
    //         },1500);
    //     }
    // },
    // ListDraw(num) {
    //     if(num == 1) {

    //     }else if(num == 2) {

    //     }
    // },
  };
  window.onload = () => {
    world.init();
  };

  // console.log(data.map((item) => console.log(item)))

  return (
    <div className="World_wrap">
      <div className="right arrow" dat_num="1"></div>
      <div className="left arrow" dat_num="2"></div>
      <div className="Last_Box main">
        {[data].map((item, idx) => {
          return <Country key={idx} ID={item.ID} Country={item.Country} />;
        })}
      </div>
      <div className="earth_img_box"></div>
    </div>
  );
}

export default World;
