import $ from "jquery";
import "../css/World.css";
import Country from "../components/country/ Country";
import { useState } from "react";
function World() {
  const world_code = ["RU", "CN", "US", "DE", "FR", "JP", "GB", "IL"];
  const [data, SetData] = useState([]);
  const [loading, Setloading] = useState(true);
  const world = {
    init() {
      this.WorldDate();
      this.animation();
    },
    WorldDate() {
      $.ajax({
        type: "GET",
        url: "https://api.covid19api.com/summary",
        dataType: "json",
        error: function (err) {
          console.error(err);
        },
        success: function (res) {
          const list = res.Countries; //
          for (let i = 0; i < list.length; i++) {
            const { CountryCode } = list[i];
            world_code.forEach((code) => {
              if (code == CountryCode) {
                SetData((data) => [list[i], ...data]);
              }
            });
          }
        },
      });
    },
    animation() {
      const items = document.getElementsByClassName("global_Date");
      alert(items);
      //   for (let i = 0; i < items.length; i++) {
      //     const element = items[i];
      //     $(element).animate({ width: "250px", height: "150px" });
      //   }
    },
  };
  window.onload = () => {
    world.init();
  };

  return (
    <div className="World_wrap">
      {loading ? <h1>loading...</h1> : <div></div>}
    </div>
  );
}

export default World;
